import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { ShieldCheck } from 'lucide-react';
import apiClient from '../services/apiClient';
import { loginAdmin } from '../redux/stateSlice.jsx';

const Admin = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const isAdminLoggedIn = useSelector((state) => state.admin.isAdminLoggedIn);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      const response = await apiClient.post('/admin/login', { username, password });
      setSuccess('Login successful');
      if (response.data?.token) {
        localStorage.setItem('token', response.data.token);
      }
      dispatch(loginAdmin());
      navigate('/admin/admin_dashboard');
    } catch (err) {
      setError('Login failed: ' + (err.response?.data?.message || err.message));
    } finally {
      setLoading(false);
    }
  };

  if (isAdminLoggedIn) {
    return (
      <div className="flex min-h-screen items-center justify-center bg-slate-50 px-4">
        <div className="w-full max-w-md rounded-2xl border border-emerald-200 bg-white p-8 text-center shadow">
          <h2 className="text-2xl font-bold text-emerald-700">Admin Session Active</h2>
          <p className="mt-2 text-sm text-slate-600">You are already logged in as admin.</p>
          <button
            onClick={() => navigate('/admin/admin_dashboard')}
            className="mt-6 w-full rounded-lg bg-emerald-600 px-4 py-2 font-semibold text-white transition hover:bg-emerald-700"
          >
            Go to Dashboard
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-100 to-blue-50 px-4 py-10 sm:py-16">
      <div className="mx-auto grid w-full max-w-5xl overflow-hidden rounded-2xl border border-blue-100 bg-white shadow-xl md:grid-cols-2">
        <div className="hidden bg-gradient-to-br from-blue-700 to-indigo-700 p-10 text-white md:flex md:flex-col md:justify-between">
          <div>
            <ShieldCheck size={42} />
            <h2 className="mt-6 text-3xl font-bold">Admin Control Center</h2>
            <p className="mt-3 text-sm text-blue-100">
              Manage scholarships, blogs, internships, and contact messages from one secure panel.
            </p>
          </div>
          <p className="text-xs text-blue-200">Use authorized credentials only.</p>
        </div>

        <div className="p-6 sm:p-10">
          <h3 className="text-2xl font-bold text-slate-800">Login As Admin</h3>
          <p className="mt-1 text-sm text-slate-500">Enter username and password to continue.</p>

          <form onSubmit={handleSubmit} className="mt-6 space-y-4">
            <div>
              <label className="mb-1 block text-sm font-medium text-slate-700">Username</label>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                name="username"
                placeholder="Enter username"
                className="w-full rounded-lg border border-slate-300 px-3 py-2 outline-none ring-blue-400 transition focus:ring-2"
                required
              />
            </div>
            <div>
              <label className="mb-1 block text-sm font-medium text-slate-700">Password</label>
              <input
                type="password"
                name="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter password"
                className="w-full rounded-lg border border-slate-300 px-3 py-2 outline-none ring-blue-400 transition focus:ring-2"
                required
              />
            </div>
            <button
              type="submit"
              disabled={loading}
              className="w-full rounded-lg bg-blue-600 py-2 font-semibold text-white transition hover:bg-blue-700 disabled:cursor-not-allowed disabled:opacity-70"
            >
              {loading ? 'Signing in...' : 'Login'}
            </button>
          </form>

          {success && (
            <div className="mt-4 rounded border border-green-300 bg-green-50 p-2 text-sm text-green-700">
              {success}
            </div>
          )}
          {error && (
            <div className="mt-4 rounded border border-red-300 bg-red-50 p-2 text-sm text-red-700">
              {error}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Admin;
