import React from 'react';
import { Link, Outlet } from 'react-router-dom';
import { useSelector } from 'react-redux';
import {
  Home,
  BookOpen,
  ClipboardList,
  Plus,
  FileText,
  Users,
  MessageSquareText,
  Edit2Icon,
  ImageIcon,
} from 'lucide-react';

const actions = [
  { icon: <Home size={20} />, label: 'Dashboard', path: '/admin/admin_dashboard' },
  { icon: <BookOpen size={20} />, label: 'Scholarships', path: '/admin/manage_scholarships' },
  { icon: <ClipboardList size={20} />, label: 'Applications', path: '/admin/view-applications' },
  { icon: <Plus size={20} />, label: 'Add Scholarship', path: '/admin/addScholarship' },
  { icon: <FileText size={20} />, label: 'Add Blog', path: '/admin/add_blog' },
  { icon: <Users size={20} />, label: 'Add Internship', path: '/admin/add_internship' },
  { icon: <Users size={20} />, label: 'Add Leadership', path: '/admin/add_leadership_data' },
  { icon: <MessageSquareText size={20} />, label: 'Messages', path: '/admin/get_message_contact' },
  { icon: <Edit2Icon size={20} />, label: 'Delete Blog', path: '/admin/delete_blog' },
  { icon: <ImageIcon size={20} />, label: 'Site & Media', path: '/admin/site-branding' },
];

const Admin_dashboar = () => {
  const isAdminLoggedIn = useSelector((state) => state.admin.isAdminLoggedIn);

  if (!isAdminLoggedIn) {
    return (
      <div className="flex h-screen items-center justify-center bg-slate-50 px-4">
        <h2 className="text-center text-lg font-bold text-red-600">Please login first as admin</h2>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-slate-100">
      <div className="mx-auto flex w-full max-w-7xl flex-col gap-4 p-3 sm:p-4 md:flex-row">
        <nav className="w-full rounded-xl bg-slate-900 p-3 text-white md:sticky md:top-4 md:h-[calc(100vh-2rem)] md:w-72 md:overflow-y-auto">
          <h2 className="mb-3 px-2 text-lg font-bold">Admin Panel</h2>
          <ul className="flex gap-2 overflow-x-auto md:block md:space-y-2 md:overflow-visible">
            {actions.map((item) => (
              <li key={item.path} className="min-w-max md:min-w-0">
                <Link
                  to={item.path}
                  className="flex items-center gap-2 rounded-lg px-3 py-2 text-sm transition hover:bg-slate-800"
                >
                  {item.icon}
                  <span>{item.label}</span>
                </Link>
              </li>
            ))}
          </ul>
        </nav>

        <section className="flex-1 space-y-4">
          <div className="grid grid-cols-2 gap-3 sm:grid-cols-3 lg:grid-cols-4">
            {actions.map((item) => (
              <Link
                to={item.path}
                key={item.path}
                className="group rounded-xl border border-slate-200 bg-white p-4 shadow-sm transition hover:-translate-y-0.5 hover:shadow-md"
              >
                <div className="mb-2 text-blue-600 group-hover:text-blue-700">{item.icon}</div>
                <p className="text-xs font-semibold text-slate-700 sm:text-sm">{item.label}</p>
              </Link>
            ))}
          </div>

          <div className="rounded-xl border border-slate-200 bg-white p-4 shadow-sm sm:p-6">
            <Outlet />
          </div>
        </section>
      </div>
    </div>
  );
};

export default Admin_dashboar;
