import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import apiClient from '../services/apiClient';
import Api_url from '../constant/constant';

const Mange_scholarship = () => {
  const [scholarships, setScholarships] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedScholarship, setSelectedScholarship] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchScholarships();
  }, []);

  const fetchScholarships = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await apiClient.get('/get/scholarship_data', { timeout: 60000 });
      setScholarships(Array.isArray(response.data) ? response.data : []);
    } catch (error) {
      const isTimeout = error?.code === 'ECONNABORTED' || String(error?.message || '').includes('timeout');
      if (isTimeout) {
        try {
          const retryResponse = await apiClient.get('/get/scholarship_data', { timeout: 60000 });
          setScholarships(Array.isArray(retryResponse.data) ? retryResponse.data : []);
          return;
        } catch (retryError) {
          setError('Server response is slow. Please try again.');
          console.error('Retry failed while fetching scholarships:', retryError);
          return;
        }
      }
      setError('Failed to fetch scholarships.');
      console.error('Error fetching scholarships:', error);
    } finally {
      setLoading(false);
    }
  };

  const safeImageUrl = (value) => {
    const resolved = Api_url.buildMediaUrl(value);
    return resolved || '/fallback-image.svg';
  };

  const confirmDelete = (item) => {
    setSelectedScholarship(item);
    setShowModal(true);
  };

  const handleDelete = async () => {
    try {
      await apiClient.delete(`/delete/scholarship/${selectedScholarship._id}`);
      setShowModal(false);
      setScholarships((prev) => prev.filter((s) => s._id !== selectedScholarship._id));
      setSelectedScholarship(null);
    } catch (error) {
      console.error('Error deleting scholarship:', error);
      alert('Failed to delete scholarship.');
    }
  };

  return (
    <div className="space-y-4">
      <div className="flex flex-wrap items-center justify-between gap-3">
        <h1 className="text-xl font-bold text-slate-800 sm:text-2xl">Manage Scholarships</h1>
        <button
          className="rounded-lg bg-slate-700 px-4 py-2 text-sm font-semibold text-white hover:bg-slate-800"
          onClick={() => navigate(-1)}
        >
          Back
        </button>
      </div>

      <div className="hidden overflow-x-auto rounded-xl border border-slate-200 bg-white shadow-sm md:block">
        <table className="min-w-full">
          <thead className="bg-slate-100 text-slate-700">
            <tr>
              <th className="p-3 text-left text-sm">Image</th>
              <th className="p-3 text-left text-sm">Name</th>
              <th className="p-3 text-left text-sm">Category</th>
              <th className="p-3 text-left text-sm">Country</th>
              <th className="p-3 text-left text-sm">Deadline</th>
              <th className="p-3 text-left text-sm">Actions</th>
            </tr>
          </thead>
          <tbody>
            {scholarships.map((item) => (
              <tr key={item._id} className="border-t hover:bg-slate-50">
                <td className="p-3">
                  <img
                    src={safeImageUrl(item.image)}
                    alt="scholarship"
                    className="h-12 w-12 rounded-md object-cover"
                    onError={(e) => { e.currentTarget.onerror = null; e.currentTarget.src = '/fallback-image.svg'; }}
                  />
                </td>
                <td className="p-3 text-sm">{item.name}</td>
                <td className="p-3 text-sm">{Array.isArray(item.category) ? item.category.join(', ') : item.category}</td>
                <td className="p-3 text-sm">{item.country}</td>
                <td className="p-3 text-sm">{item.deadline ? new Date(item.deadline).toLocaleDateString() : 'N/A'}</td>
                <td className="p-3">
                  <button
                    onClick={() => confirmDelete(item)}
                    className="rounded-md bg-red-600 px-3 py-1 text-xs font-semibold text-white hover:bg-red-700"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="grid gap-3 md:hidden">
        {scholarships.map((item) => (
          <div key={item._id} className="rounded-xl border border-slate-200 bg-white p-3 shadow-sm">
            <div className="flex gap-3">
              <img
                src={safeImageUrl(item.image)}
                alt={item.name}
                className="h-14 w-14 rounded object-cover"
                onError={(e) => { e.currentTarget.onerror = null; e.currentTarget.src = '/fallback-image.svg'; }}
              />
              <div className="min-w-0">
                <p className="truncate text-sm font-semibold text-slate-800">{item.name}</p>
                <p className="text-xs text-slate-500">{item.country || 'N/A'}</p>
                <p className="text-xs text-slate-500">
                  {item.deadline ? new Date(item.deadline).toLocaleDateString() : 'N/A'}
                </p>
              </div>
            </div>
            <button
              onClick={() => confirmDelete(item)}
              className="mt-3 w-full rounded-md bg-red-600 px-3 py-1.5 text-xs font-semibold text-white hover:bg-red-700"
            >
              Delete
            </button>
          </div>
        ))}
      </div>

      {loading && (
        <div className="rounded-lg border border-slate-200 bg-white p-6 text-center text-sm text-slate-500">
          Loading scholarships...
        </div>
      )}

      {!loading && error && (
        <div className="rounded-lg border border-red-200 bg-red-50 p-6 text-center text-sm text-red-700">
          {error}
        </div>
      )}

      {!loading && !error && scholarships.length === 0 && (
        <div className="rounded-lg border border-slate-200 bg-white p-6 text-center text-sm text-slate-500">
          No scholarships found.
        </div>
      )}

      {showModal && selectedScholarship && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 p-4">
          <div className="w-full max-w-md rounded-xl bg-white p-6 text-center shadow-lg">
            <h2 className="text-lg font-semibold text-slate-800">Confirm Deletion</h2>
            <p className="mt-2 text-sm text-slate-600">
              Are you sure you want to delete <strong>{selectedScholarship.name}</strong>?
            </p>
            <div className="mt-6 flex justify-center gap-3">
              <button
                onClick={handleDelete}
                className="rounded-md bg-red-600 px-4 py-2 text-sm font-semibold text-white hover:bg-red-700"
              >
                Yes, Delete
              </button>
              <button
                onClick={() => setShowModal(false)}
                className="rounded-md bg-slate-200 px-4 py-2 text-sm font-semibold text-slate-700 hover:bg-slate-300"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Mange_scholarship;
