import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import apiClient from '../services/apiClient';

const Message = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const messages = useSelector((state) => state.admin.isAdminLoggedIn);

  const getData = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await apiClient.get('/getMessage');
      setData(Array.isArray(response.data) ? response.data : []);
    } catch (err) {
      setError('Failed to load messages');
    } finally {
      setLoading(false);
    }
  };

  const deleteMessage = async (id) => {
    try {
      const response = await apiClient.delete(`/deleleMessage/${id}`);
      if (response.status === 200) {
        setData((prev) => prev.filter((item) => item._id !== id));
      }
    } catch (err) {
      setError('Failed to delete message');
    }
  };

  useEffect(() => {
    getData();
  }, []);

  if (!messages) {
    return <p className="text-sm text-slate-600">Please log in first to manage user messages.</p>;
  }

  return (
    <div className="space-y-4">
      <button
        onClick={() => navigate(-1)}
        className="rounded-lg bg-slate-700 px-4 py-2 text-sm font-semibold text-white transition hover:bg-slate-800"
      >
        Back
      </button>

      <div className="rounded-xl border border-slate-200 bg-white p-4 shadow-sm sm:p-5">
        <h2 className="mb-4 text-xl font-bold text-slate-800">Messages</h2>

        {loading && <p className="text-sm text-slate-500">Loading messages...</p>}
        {error && <p className="mb-3 text-sm text-red-600">{error}</p>}

        {!loading && data.length === 0 ? (
          <p className="text-sm text-slate-500">No messages found.</p>
        ) : (
          <div className="space-y-3">
            {data.map((item) => (
              <div key={item._id} className="rounded-lg border border-slate-200 p-3 sm:p-4">
                <div className="grid gap-2 sm:grid-cols-2">
                  <p className="text-sm"><span className="font-semibold">Name:</span> {item.name || 'N/A'}</p>
                  <p className="text-sm"><span className="font-semibold">Email:</span> {item.email || 'N/A'}</p>
                  <p className="text-sm sm:col-span-2"><span className="font-semibold">Website:</span> {item.website || 'N/A'}</p>
                  <p className="text-sm sm:col-span-2"><span className="font-semibold">Message:</span> {item.message || 'N/A'}</p>
                </div>
                <div className="mt-3">
                  <button
                    onClick={() => deleteMessage(item._id)}
                    className="rounded-md bg-red-600 px-3 py-1.5 text-xs font-semibold text-white transition hover:bg-red-700"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Message;
